import React from 'react'
import { Component } from 'react/cjs/react.production.min';

class music extends Component {

    constructor(props) {
        super(props);
        this.state = {
    
          play: false,
          pause: true
    
        };
    
        this.url = "https://soundcloud.com/mikereveriemr/diplo-sidepiece-on-my-mind-mike-reverie-x-olly-p-flip?utm_source=clipboard&utm_medium=text&utm_campaign=social_sharing";
        this.audio = new Audio(this.url);
    }
    play(){
        this.setState({
          play: true,
          pause: false
        });
        console.log(this.audio);
        this.audio.play();
      }
      
      pause(){
      this.setState({ play: false, pause: true });
        this.audio.pause();
      }
      
      render() {
        
      return (
        <div>
          <button onClick={this.play}>Play</button>
          <button onClick={this.pause}>Pause</button>
        </div>
        );
      }
    }
    
    
    export default music;
