import React,{Component} from 'react';
import {Link,withRouter} from 'react-router-dom'
import audio from '../static/audio.mp3'
class sleepStories extends Component{






// Import your audio file



  state = {

    // Get audio file in a variable
    audio1: new Audio(audio),
    audio2: new Audio(audio),
    // Set initial state of song
    isPlaying: false,
  };

  // Main function to handle both play and pause operations
  playPause = () => {

    // Get state of song
    let isPlaying = this.state.isPlaying;

    if (isPlaying) {
      // Pause the song if it is playing
      this.state.audio1.pause();
    } else {

      // Play the song if it is paused
      this.state.audio1.play();
    }

    // Change the state of song
    this.setState({ isPlaying: !isPlaying });
  };

  render() {
    return (
     <div>
     <div>
        {/* Show state of song on website */}
       
       <div>Violin Ambience   Duration  </div>
        <p>
          {this.state.isPlaying ? 
            "Song is Playing" : 
            "Song is Paused"}
        </p>

        {/* Button to call our main function */}
        <button onClick={this.playPause}>
          Play | Pause
        </button>
      </div>
      <div>Violin Ambience  </div>
      <p>
        {this.state.isPlaying ? 
          "Song is Playing" : 
          "Song is Paused"}
      </p>

      {/* Button to call our main function */}
      <button onClick={this.playPause}>
        Play | Pause
      </button>




      <div>Violin Ambience  </div>
        <p>
          {this.state.isPlaying ? 
            "Song is Playing" : 
            "Song is Paused"}
        </p>

        {/* Button to call our main function */}
        <button onClick={this.playPause}>
          Play | Pause
        </button>
      </div>
    //</div>
    );
  }
}









export default sleepStories;
