import React from 'react'
import {Link} from 'react-router-dom'

// import ReactDOM from'reactdom'
class Medtimer extends React.Component {
constructor(props){
  super(props)
  this.state={
   entry: '0' 
  }
this.onSubmittion=this.onSubmittion.bind(this)
}
onSubmittion(event){
  event.preventDefault();
  this.setState({entry:event.target.value})

}
componentDidMount(){
  
    this.myInterval=setInterval(()=>{
    this.setState(prevState=>({entry:prevState.entry+1}))
  },1000)
  console.log(this.state.entry)
}

render(){
   
  return(
    <div>
            <form >
            <input type='number' value={this.state.entry} onChange={this.onSubmittion}></input>
            <button type='submit' >submit</button>



            </form>

    </div>
  )
}


}
  export default Medtimer;
